import { useLoaderData } from "react-router-dom";
import EventsList from "../components/EventsList";

function EventsPage() {
  //Gebruik de geladen data van de loader functie.
  const events = useLoaderData();
  //Render de EventsList-component met de geladen evenementen.
  return (
    <div>
      <EventsList events={events} />
    </div>
  );
}

export default EventsPage;

//Functie voor het ophalen van evenementengegevens

export async function loader() {
  const response = await fetch("http://localhost:8080/events");
  // Geef een foutmelding terug als het ophalen van evenementen mislukt
  if (!response.ok) {
    return { isError: true, message: "Cloud not fetch events." };
  } else {
    // Verwerk het antwoord en geef de evenementengegevens terug
    const resData = await response.json();
    return resData.events;
  }
}
