import React from "react";
import classes from "./HeroSection.module.css";
import ImageCountry from "../img/country.png";
import { Link } from "react-router-dom";

function HeroSection() {
  return (
    <div className={classes.hero_section}>
      <h1 className={classes.h1_hero}>Welcome to our Events Page!</h1>
      <p className={classes.p_hero}>
        Get ready for an extraordinary journey through the world of country
        music and culture. Here, you'll find a carefully curated selection of
        upcoming events that will immerse you in the essence of this beloved
        genre.
      </p>
      <Link to="/events">
        {" "}
        <button className={classes.hero_button}>All Events</button>
      </Link>
    </div>
