import React from "react";
import classes from "./HeroSection.module.css";
import ImageCountry from "../img/country.png";
import { Link } from "react-router-dom";

function HeroSection() {
  return (
    <div>
      <div className={classes.hero_section}>
        <div className={classes.HeroContent}>
          <h1 className={classes.h1_hero}>
            Experience the Future of Country Events!
          </h1>
          <p className={classes.p_hero}>
            Discover the magic of upcoming events in the world of country music
            and culture. Immerse yourself in the vibrant melodies, captivating
            performances, and heartwarming experiences that await you.
          </p>
          <Link to="/events">
            {" "}
            <button className={classes.HeroButton}>All Events</button>
          </Link>
        </div>
      </div>
      <section>
        <div>
          <img
            src={ImageCountry}
            alt="ImageCoutry"
            className={classes.ImageCountry}
          />
        </div>
      </section>
    </div>
  );
}
