import React from "react";
import classes from "./HeroSection.css";
import ImageCountry from "../img/country.png";

function HeroSection() {
  return (
    <header className="HeroSection">
      <div className="HeroContent">
        <h1 className="HeroH1">Experience the Future of Country Events!</h1>
        <p className="HeroP">
          Discover the magic of upcoming events in the world of country music
          and culture. Immerse yourself in the vibrant melodies, captivating
          performances, and heartwarming experiences that await you.
        </p>
        <button className="HeroButton" to="/events">
          All Events
        </button>
      </div>
      <div>
        <img src={ImageCountry} alt="ImageCoutry" className="ImageCountry" />
      </div>
    </header>
  );
}
